$spacer: 1rem;

// Variables for description text truncation
$lh: 22;
$line-height: 22px;

$max-height: 180px;
$mh: 180;
$max-num-lines: floor($mh / $lh);

$max-height-tablet: 90px;
$mht: 90;
$max-num-lines-tablet: floor($mht / $lh);

$max-height-desktop: 68px;
$mhd: 68;
$max-num-lines-desktop: floor($mhd / $lh);

.module-container {
  padding: ($spacer * 2) 15px;
}

ul {
  padding-inline-start: 0;
  margin: 0 $spacer;
}

.row {
  justify-content: center;
}

.headline {
  font-size: 2em;
  margin-bottom: $spacer;

  @include media-breakpoint-down(sm) {
    text-align: center;
  }
}

.item-col {
  margin-bottom: $spacer;
  @include media-breakpoint-down(sm) {
    text-align: center;
  }
}

.section {
  margin: $spacer $spacer 0;
  font-size: 1em;
  word-break: break-word;
}

.item {
  height: 100%;
  position: relative;
  background-color: #f2f2f2;
  text-align: left;
  display: flex;
  flex-direction: column;
}

.image {
  object-fit: contain;
  max-width: 100%;
  width: 100%;

  &-container {
    flex-shrink: 0;
  }
}

.content-container {
  height: 100%;
  display: flex;
  flex-direction: column;
  padding-bottom: $spacer;
}

.title {
  font-size: 1.5em;
  font-weight: normal;
  word-break: break-word;
}

.subtitle {
  font-size: 1.25em;
  font-weight: normal;
}

.address {}

.phone {}

.email {}

.description, .description-text {
  max-height: $max-height;

  @include media-breakpoint-up(sm) {
    max-height: $max-height-tablet;
  }

  @include media-breakpoint-up(lg) {
    max-height: $max-height-desktop;
  }
}

.description-text {
  -webkit-line-clamp: $max-num-lines;
  display: -webkit-box;
  -webkit-box-orient: vertical;
  overflow: hidden;

  @include media-breakpoint-up(sm) {
    -webkit-line-clamp: $max-num-lines-tablet;
  }

  @include media-breakpoint-up(lg) {
    -webkit-line-clamp: $max-num-lines-desktop;
  }
}

.text-list {}

.list-item {
  margin-left: -5px;
}

.cta {
  font-size: 1em;
  text-decoration: none;

  &-container {
    display: flex;
    align-items: flex-end;
    flex: 1;
  }
}

